(59, 50, 'exp_templates', 'template_data', 1400264290, 1, '{embed=''includes/xhtml'' page_title="Latest News"}\n\n{embed=''includes/header''}\n<div class="wrapper">\n  <div class="interior-content">\n    <div id="mainColumn"> {if segment_2 ==""}\n      <h1>Latest News </h1>\n      <div id="news" class="wide"> {exp:channel:entries channel="news" limit="10"}\n        <div class="news-story">\n          <h2><a href="{site_url}latest-news/{url_title}">{title}</a></h2>\n          <span class="date"> By {news_author}, {if author_suffix == "Yes"} CPA,{/if} {author_position}, {entry_date format="%F %d, %Y"}<br />\n          {exp:channel:categories style="linear" show_empty="no"} <a href="{category_url_title}">{category_name}</a> {/exp:channel:categories} </span> {exp:word_limit total="30"}{news_body}{/exp:word_limit}\n          <p class="readmore"><a href="{url_title}">Read More &raquo;</a></p>\n        </div>\n        <!-- end #news-story --> \n        {/exp:channel:entries} </div>\n      <!-- end #news --> \n      {/if}\n      {if segment_2 !=""} \n      <script>\n        $(function() {\n        $( "#tabs" ).tabs({\n          collapsible: true,\n          active: false\n        });\n        });\n        </script>\n      <div id="news" class="wide"> {exp:channel:entries channel="news"}\n        <div class="news-story last pxBuffer">\n          <h1>{title}</h1>\n          <span class="date">By {news_author}, CPA, Partner, {entry_date format="%F %d, %Y"}</span><br />\n          {if news_image != ""}\n          <div class="alignright"><img src="{news_image}" width="100" height="130"/></div>\n          {/if} \n          \n          {news_body}\n          <div id="tabs">\n            <ul>\n              <li id="first"><a href="#tabs-1">Give Article Feedback</a> &raquo;</li>\n              <li>|</li>\n              <li><a href="#tabs-2">E-mail Umbaugh</a> &raquo;</li>\n              <li>|</li>\n              <li><a href="javascript:window.print()">Print Article</a> &raquo;</li>\n            </ul>\n            <div id="tabs-1" class="tab_content" style="display: none;">\n            <div class="contact_us-form">\n              <h3>Send your feedback:</h3>\n              {exp:freeform:form\n              form_id="2"\n              required="name|email"\n              recipient_template="contact_form"\n              notify_user="yes"\n              user_email_field="email"\n              inline_errors="yes"\n              admin_notify="dannym@quinlanmarketing.com" \n              }\n              <input type="hidden" name="articleName" value="{title}">\n              <div class="submit-form">\n                <div class="radio_list">Is this item worthy of implementation?\n                  <div class="radio_box">\n                    <input type="radio" name="implementation" id="vote_1" value="Yes" />\n                    <div class="radio_label">\n                      <label for="vote_1">Yes</label>\n                    </div>\n                    <input type="radio" name="implementation" id="vote_2" value="No" />\n                    <label for="vote_2">No</label>\n                    <input type="radio" name="implementation" id="vote_3" value="Maybe" />\n                    <label for="vote_3">Maybe</label>\n                  </div>\n                  <!--radio-box--> \n                </div>\n                <!--radio-list implementation-->\n                \n                <div class="radio_list">Is this item worth sharing with other associates?\n                  <div class="radio_box">\n                    <input type="radio" name="sharing" id="vote_1" value="Yes" />\n                    <label for="vote_1">Yes</label>\n                    <input type="radio" name="sharing" id="vote_2" value="No" />\n                    <label for="vote_2">No</label>\n                    <input type="radio" name="sharing" id="vote_3" value="Maybe" />\n                    <label for="vote_3">Maybe</label>\n                  </div>\n                  <!--radio-box--> \n                </div>\n                <!--radio-list sharing-->\n                \n                <div class="radio_list">Did this item present value to you and your business?\n                  <div class="radio_box">\n                    <input type="radio" name="value" id="vote_1" value="Yes" />\n                    <label for="vote_1">Yes</label>\n                    <input type="radio" name="value" id="vote_2" value="No" />\n                    <label for="vote_2">No</label>\n                    <input type="radio" name="value" id="vote_3" value="Maybe" />\n                    <label for="vote_3">Maybe</label>\n                  </div>\n                  <!--radio-box--> \n                </div>\n                <!--radio-list value--> \n              </div>\n              <!--submit-form-->\n              <div class="submit-form_left">\n                <label>{freeform:label:email}</label>\n                {if freeform:error:email} <span class="error"> {freeform:error:email} </span> {/if}\n                {freeform:field:email}\n                <label>{freeform:label:user_message}</label>\n                <label>{freeform:field:user_message}</label>\n                <input type="submit" value="Submit" class="submit" >\n              </div>\n              <!--submit-form_left--> \n              {/exp:freeform:form} \n			</div><!--.contact_us-->\n            </div>\n            <!--tabs-1-->\n            <div id="tabs-2" class="tab_content" style="display: none;">\n            <div class="contact_us-form ">\n              <h3> E-mail Umbaugh</h3>\n              {exp:freeform:form\n              form_id="3"\n              }\n              <div class="submit-form">\n                <label>{freeform:label:name}</label>\n                {if freeform:error:name} <span class="error"> {freeform:error:name} </span> {/if}\n                {freeform:field:name}\n                <label>{freeform:label:email}</label>\n                {if freeform:error:email} <span class="error"> {freeform:error:email} </span> {/if}\n                {freeform:field:email}\n                <label>{freeform:label:phone}</label>\n                {freeform:field:phone}\n                <label>{freeform:label:organization}</label>\n                {freeform:field:organization} </div>\n              <div class="submit-form_left">\n                <label>{freeform:label:user_message}</label>\n                <label>{freeform:field:user_message}</label>\n                <input type="submit" value="Submit" class="submit" >\n              </div>\n              {/exp:freeform:form}\n              </div><!--.contat_us-->\n            </div>\n            <!--tabs-2--> \n          </div>\n          <!--#tabs-->\n          \n          <p><em>Information in this article was believed current as of the date of publication.  As you know, changes occur frequently.  The information presented is of a general educational nature.\n            Before applying to your specific circumstances, please contact us at <a href="mailto:footnotes@umbaugh.com">footnotes@umbaugh.com.</a></em></p>\n        </div>\n        <!--news-story last pxBuffer --> \n        {/exp:channel:entries} </div>\n      <!-- end #news--> \n      {/if} </div>\n    <!-- end #mainColumn -->\n    \n    <div id="sideColumn">\n      <div id="nav_menu-4" class="widget widget_nav_menu">\n        <div class="menu-sidebar-menu-container">\n          <ul id="menu-sidebar-menu" class="menu">\n            <li id="menu-item-255" class="menu-item"><a href="/services">Our Services</a></li>\n            <li id="menu-item-64" class="menu-item"><a href="/contact">Contact Us</a></li>\n            <li id="menu-item-65" class="menu-item"><a href="/join-our-firm">Join Our Firm</a></li>\n          </ul>\n        </div>\n      </div>\n      <img src="../images/dotted-seperator.png" /><br />\n      <h2>Catagories</h2>\n      {exp:channel:categories channel="news"} <a href="{path=''latest-news/index''}">{category_name}</a> {/exp:channel:categories} <img src="../images/dotted-seperator.png" /><br />\n      <h2>News Archive</h2>\n      <img src="../images/dotted-seperator.png" /><br />\n      <h2>Newsletter Sign-Up</h2>\n      <p>To receive our Footnotes Newsletters please provide us with your email address:</p>\n      <form action="http://campaign.q-ux.com/t/r/s/tduypj/" method="post">\n        <p>\n          <label for="fieldEmail">Email</label>\n          <br />\n          <input id="fieldEmail" name="cm-tduypj-tduypj" type="email" required />\n        </p>\n        <p>\n          <button type="submit" class="wpsb_form_btn">Submit</button>\n        </p>\n      </form>\n    </div>\n  </div>\n</div>\n<!--wrapper--> \n\n{embed=''includes/footer''}'),
