(64, 55, 'exp_templates', 'template_data', 1400264290, 1, '// Banner Tabs handling\n$(''.banner-tab-label'').click(function(e) {\n    e.preventDefault();\n\n    var $label = $(this).closest(''li'');\n    var $tab = $($(this).attr(''href''));\n\n    var $activeTab = $tab.siblings(''.active'');\n    var $activeLabel = $label.siblings(''.active'');\n\n    $activeLabel.removeClass(''active'');\n    $label.addClass(''active'');\n\n    $activeTab.fadeOut(1000).removeClass(''active'');\n    $tab.fadeIn(1000).addClass(''active'');\n});'),
(65, 67, 'exp_templates', 'template_data', 1400264290, 1, '{embed=''includes/xhtml'' page_title="Search Results"}\n\n{embed=''includes/header''}\n<div class="wrapper">\n  <div class="interior-content">\n    <div id="mainColumn">\n      <div class="wide" id="news">\n        <h1>Search Results for: <span>{exp:search:keywords}{/exp:search:keywords}</span></h1>\n        {exp:search:search_results}\n        <div class="news-story">\n          <h2><a href="{auto_path}">{title}</a></h2>\n          <span class="date">{entry_date format="%m/%d/%y"}<br />\n          {categories}\n          {category_image} <a href="{path=''channel/index''}">{category_name}</a> {/categories}\n          {exp:word_limit total="30"}{news_body}{/exp:word_limit}\n          <p class="readmore"><a href="{auto_path}">Read More &raquo;</a></p>\n          </p>\n        </div>\n        {if count == total_results} </div>\n      <!-- div wide id news --> \n      {/if}\n      \n      {paginate}\n      <p>Page {current_page} of {total_pages} pages {pagination_links}</p>\n      {/paginate}\n      \n      {/exp:search:search_results} </div>\n    <!-- div wide id news --> \n  </div>\n  <!-- main column --> \n</div>\n<!-- interior-content -->\n</div>\n<!-- wrapper --> \n<!--wrapper--> \n\n{embed=''includes/footer''}'),
